public class LibreriaMath {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//Funciones matematicas : Libreria Math
		double numero = Math.sqrt(34);
		
		long numero2 = (long)Math.pow(4, 2);
		
		int numero3 = (int)Math.round(numero);
		System.out.println("La variable numero3 vale \n" + numero3);
		System.out.println(numero + "\n" + numero2);
	}

}
